//import Header from '../components/Header';
import React, {useState, useEffect} from 'react';
//import me from '../images/me.png';


const Dictionary = () => {
    //MyForm();

    const [formData, setFormData] = useState({
        kanji: ''
    });

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted:', formData);
        setFormData({
            kanji: e
        });
        return formData;
    };


    return (    
        <div className="dictionaryContainer">
        <form onSubmit={handleSubmit}>
            <label htmlFor="kanjiInput">Enter Kanji:</label>
            <input
                type="text"
                id="kanjiInput"
                value={formData.kanji}
                onChange={(e) => setFormData({ kanji: e.target.value })}
            />
            <button type="submit">Search</button>
        </form>
        <div id="output">
            {/* Display search results or output here */}
        </div>
        </div>

           // <div className = "dictonaryContainer">
        //     <body>
        //         <div id="output">
        //             <script>

        //             </script>
        //         </div>
        //     </body>
        // </div>
    );
}

const process = async(submit) => {
    const options = {
    method: 'GET',
    headers: {
        'X-RapidAPI-Key': '6a12f8c88cmsh830de894db5186bp10d491jsnfd2c65de1956',
        'X-RapidAPI-Host': 'kanjialive-api.p.rapidapi.com'
    }
    };

    const url = 'https://kanjialive-api.p.rapidapi.com/api/public/kanji/'; 
    const uri = url.concat(submit); //%E4%BC%91';
    console.log(url);
    //const encoded = encodeURI(url);
    try {
        const response = await fetch(uri, options);
        const result = await response.json();
        var outputElement = document.getElementById("output");

        var kanji = result.ka_utf; //kanji character
        var on = result.onyomi_ja; //chinese reading
        var kun = result.kunyomi_ka_display; // japanese reading
        var examples = result.examples; //words that it is in 
        var related_words = " ";

        for (var word in examples){
            related_words += examples[word].japanese;
            related_words += examples[word].meaning.english;
            related_words += ", ";
        }
        var wordDes = kanji + ":" + on + ":" + kun + ":" + related_words;

        outputElement.innerHTML = wordDes;
    } catch (error) {
        console.error(error);
    }
}

export default Dictionary;
