import React, {useState, useEffect} from 'react';
//const fs = require('fs');

const About = () => {
    var map = {};
    map["本"] = "木 'tree' , 一 'cut through'";

    const [formData, setFormData] = useState({
        kanji: ''
    });

    const handleSubmit = (x) => {
        x.preventDefault();
        console.log('Form submitted:', formData);
        helper(formData, map);
    };
    return (    
        <div className = "about">
             <form onSubmit={handleSubmit}>
                <label htmlFor="kanjiInput"> Enter Kanji : </label>
                <input
                    type="text"
                    id="kanjiInput"
                    value={formData.kanji}
                    onChange={(e) => setFormData({ kanji: e.target.value })}
                />
                <button type="submit"> Search </button>
            </form>
        </div>
    );
}

const initializeSet = () => {
    //https://stackoverflow.com/questions/8877666/how-is-a-javascript-hash-map-implemented 
    //hash map ^
    var map = {};
    map["本"] = "木 'tree' , 一 'cut through'";
    const result = kanji.json;
    // for(var i = 0; i < 200; i++){
    //     //set up the hash set from kanji.txt
    // }
}

const helper = async(formData, map) => {
    const submit = formData.kanji;
    //get from txt file 
    if(submit in map){
        //call API on that kanji
        const options = {
            method: 'GET',
            headers: {
                'X-RapidAPI-Key': '6a12f8c88cmsh830de894db5186bp10d491jsnfd2c65de1956',
                'X-RapidAPI-Host': 'kanjialive-api.p.rapidapi.com'
            }
            };
        //print stuff to html
        const url = 'https://kanjialive-api.p.rapidapi.com/api/public/kanji/'; 
        const uri = url.concat(submit); 
        console.log(url); //const encoded = encodeURI(url);
        try {
            const response = await fetch(uri, options);
            const result = await response.json();
            if(!response.ok){
                let outputElement = document.getElementById("output");
                let err = "Please type a valid character."
                outputElement.innerHTML = err;
            }
            else{
                let outputElement = document.getElementById("outputKanji");
                let on = result.onyomi_ja; //chinese reading
                let defintion = result.kanji.meaning.english;
                let kun = result.kunyomi_ka_display; // japanese reading
                let words = result.examples; //words that it is in 
                let examples = "";
                for(var x in words){
                    var max = 5;
                    examples += x;
                    max++;
                }

                let wordDes = ` 
                <p id= "def">  Meaning: ${defintion}</p>
                <p id= "on"> On reading: ${on}</p>
                <p id= "kun"> Kun reading: ${kun}</p> 
                <p id= "relate"> Words with this character: ${examples}</p>
                `; //template string
                console.log(wordDes);
                outputElement.innerHTML = wordDes;
        }
        }
        catch(error){
            console.log("there was malfunction");
        }
    } 
    else{
        console.log("Not in base set");
    }
    //store in the cache  
}
export default About;
