import React, {useState, useEffect} from 'react';


const About = () => {
    initializeSet();
    const [formData, setFormData] = useState({
        kanji: ''
    });
    const handleSubmit = (x) => {
        x.preventDefault();
        console.log('Form submitted:', formData);
        process(formData);
    };
    return (    
        <div className = "about">
             <form onSubmit={handleSubmit}>
                <label htmlFor="kanjiInput"> Enter Kanji : </label>
                <input
                    type="text"
                    id="kanjiInput"
                    value={formData.kanji}
                    onChange={(e) => setFormData({ kanji: e.target.value })}
                />
                <button type="submit"> Search </button>
            </form>
        </div>
    );
}

const initializeSet = () => {
    //https://stackoverflow.com/questions/8877666/how-is-a-javascript-hash-map-implemented 
    //hash map ^
    var map = {};
    map["本"] = "木 'tree' , 一 'cut through'";
    // for(var i = 0; i < 200; i++){
    //     //set up the hash set from kanji.txt
    // }
}

const helper = () => {
    const submit = formData.kanji;
    //get from txt file 
    if(submit in map){
        //call API on that kanji
        console.log(map[submit]);
        //print stuff to html
    } 
    else{
        console.log("Not in base set");
    }
    //store in the cache  
}
export default About;
